    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        /*
         * awful code here. Would need a recursive system to go through an entire tree, and recreate it fully.
         */
        
        File file=ADTConverterApp.chooseFile();
        if (file!=null) {
            if (file.isFile()) {
                ADTConverterApp.converter=ADTConverter.initConverter(ADTConverterApp.fileType);
                ADTConverterApp.converter.convertADT(file);

            } else {
                if (file.isDirectory()) {

                    String dirName=file.getName();
                    new File(ADTConverterApp.config.getProperty("directory")+dirName).mkdir();
                    ADTConverterApp.relativeDir=dirName+System.getProperty("file.separator");

                    File[] files = file.listFiles();
                    if (files != null) {
                        for (File currentFile : files) {
                            String fileExt=currentFile.getName().substring(currentFile.getName().length()-3);
                            if (fileExt.equals("adt")) {
                                ADTConverterApp.converter=ADTConverter.initConverter(ADTConverterApp.fileType);
                                ADTConverterApp.converter.convertADT(currentFile);
                            } else {
                                System.out.println(currentFile.getName()+" is not an adt file : no parsing.");
                            }

                        }
                    } else {
                        System.out.println("No file in selected folder");
                    }

                } else { // neither a file nor a directory? Wait...
                    System.out.println("Selected file is not valid.");
                }
            }
        }
    }//GEN-LAST:event_jMenuItem1ActionPerformed

